import React, { useState, useReducer } from "react";
import { Container } from "./AppStyle";

const App = () => {
  const [state, setState] = useState(0);
  const decreament =()=>{
    setState(state-1)
  }
  return (
    <Container>
      <button onClick={decreament}>-</button>
      <span>{state}</span>
      <button onclick={increament}>+</button>
    </Container>
  );
};

export default App;
