import React, { useState, useContext } from "react";
import {
  Container,
  IconContainer,
  MobileContainer,
  Icon,
  CatImg,
  ImgDiv,
  TitleImg,
  Desc,
  Item,
  Title,
  Line,
  ToggleMode,
} from "./AppStyle";
import Cat from "./cat.jpg";
import { ModeContext } from "./context";

const App = () => {
  const [mode, setMode] = useState(false);
  const [theme, setTheme] = useContext(ModeContext);
  const dark = {
    icon: "black",
    title: "black",
    background: "white",
  };

  const light ={
    icon: 'white',
    title:'white',
    background: 'black',
  }

  const setIsDarkMode = () => {
    setMode(!mode);
    setTheme(mode)
  };
  return (
    <Container>
      <MobileContainer>
        <IconContainer>
          <Icon.Arrow />
          <Icon.Switch />
        </IconContainer>
        <ImgDiv>
          <CatImg src={Cat} alt="cat" />
          <TitleImg>Catty</TitleImg>
          <Desc>CatLover</Desc>
        </ImgDiv>
        {/* darkMode */}
        <Item>
          <Icon.Moon />
          <Title>Dark Mode</Title>
          <ToggleMode onChange={setIsDarkMode} checked={mode} size={40} />
        </Item>
        {/* Grid */}
        <Item>
          <Icon.Grid />
          <Title>Story</Title>
        </Item>
        {/* Chat */}
        <Item>
          <Icon.User />
          <Title>Chat heads</Title>
        </Item>
        {/* Groups */}
        <Item>
          <Icon.Userplus />
          <Title>Groups</Title>
        </Item>
        <Line></Line>
        {/* Media */}
        <Item>
          <Icon.Media />
          <Title>Media and photos</Title>
        </Item>
        {/* Settings */}
        <Item>
          <Icon.Setting />
          <Title>Settings and Privacy</Title>
        </Item>
        {/* Comments */}
        <Item>
          <Icon.Comments />
          <Title>Help Centre</Title>
        </Item>
        {/* Notifications */}
        <Item>
          <Icon.Notification />
          <Title>Notifications</Title>
        </Item>
      </MobileContainer>
    </Container>
  );
};

export default App;
