import styled from "styled-components";
import { Settings } from "@styled-icons/evaicons-solid/Settings2";
import { KeyboardArrowLeft } from "@styled-icons/material/KeyboardArrowLeft";
import { Switch } from "@styled-icons/icomoon/Switch";
import { Moon } from "@styled-icons/boxicons-solid/Moon";
import { Grid } from "@styled-icons/bootstrap/Grid";
import { UserCheck } from "@styled-icons/boxicons-regular/UserCheck";
import { UserPlus } from "@styled-icons/boxicons-regular/UserPlus";
import { PermMedia } from "@styled-icons/material-sharp/PermMedia";
import { CommentDots } from "@styled-icons/boxicons-regular/CommentDots";
import { Notifications } from "@styled-icons/ionicons-sharp/Notifications";
import { Excerpt } from "@styled-icons/open-iconic";

export const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100wh;
  height: fit-content;
  padding: 100px 300px;
`;

export const MobileContainer = styled.div`
  width: 400px;
  height: 600px;
  background-color: whitesmoke;
  box-shadow: 5px 5px 12px 6px rgba(0, 0, 0, 0.25);
`;

export const IconContainer = styled.div`
  display: flex;
  width: 100%;
  padding: 20px;
`;

export const Icon = styled.div``;

Icon.Arrow = styled(KeyboardArrowLeft)`
  color: black;
  width: 30px;
  height: 30px;
  cursor: pointer;
`;

Icon.Switch = styled(Switch)`
  color: black;
  width: 18px;
  height: 18px;
  cursor: pointer;
  margin-left: auto;
`;

export const ImgDiv = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  margin-top: 30px;
`;

export const CatImg = styled.img`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  border: 2px solid lightgrey;
`;

export const TitleImg = styled.div`
  font-size: 22px;
  font-weight: bold;
  font-family: SF Pro Display;
`;

export const Desc = styled.div`
  font-size: 14px;
  font-weight: normal;
  font-family: SF Pro Display;
`;

export const Item = styled.div`
  display: flex;
  padding: 20px;
`;

export const Title = styled.div`
  font-size: 16px;
  font-weight: normal;
  font-family: SF Pro Display;
  padding-left: 15;
`;

Icon.Moon = styled(Moon)`
  width: 20px;
  height: 20px;
  color: black;
`;
